INSERT INTO `product` VALUES ('26', 'Chamomile     10ml    BD006', 'images/product/20111126193114816.jpg', null, null, '63', '<p style=\\\"margin:0cm 0cm 0pt;\\\" class=\\\"MsoNormal\\\">\r\n	<span class=\\\"apple-style-span\\\"><span style=\\\"font-family:Arial;background:white;color:#221f1f;font-size:9pt;\\\" lang=\\\"EN-US\\\">German chamomile (Matricaria recutita) has been used to support the body’s natural response to irritation and injury. This oil contains chamazulene, a compound studied for its ability to help the body’s natural inflammatory response.<?xml:namespace prefix = o ns = \\\"urn:schemas-microsoft-com:office:office\\\" /><o:p></o:p></span></span>\r\n</p>', '<p style=\\\"margin:0cm 0cm 0pt;\\\" class=\\\"MsoNormal\\\">\r\n	<span class=\\\"apple-style-span\\\"><span style=\\\"font-family:Arial;background:white;color:#221f1f;font-size:9pt;\\\" lang=\\\"EN-US\\\">Dietary: Put two drops in a capsule. Take three times daily or as needed. Topical: Apply 2–4 drops to desired area. Dilution not required except for the most sensitive skin. Aromatic: Diffuse up to one hour three times daily. Keep out of reach of children. If you are pregnant, nursing, taking medication, or have a medical condition, consult a health-care practitioner prior to use.<?xml:namespace prefix = o ns = \\\"urn:schemas-microsoft-com:office:office\\\" /><o:p></o:p></span></span>\r\n</p>', '<p style=\\\"margin:0cm 0cm 0pt;\\\" class=\\\"MsoNormal\\\">\r\n	<span class=\\\"apple-style-span\\\"><span style=\\\"font-family:Arial;color:#221f1f;font-size:9pt;\\\" lang=\\\"EN-US\\\">BTW 100% Pure Natural Organic<span style=\\\"background:white;\\\"> German Chamomile (Matricaria recutita flower) essential oil<?xml:namespace prefix = o ns = \\\"urn:schemas-microsoft-com:office:office\\\" /><o:p></o:p></span></span></span>\r\n</p>', '2011-11-26 19:30:01', '', 'en', 'BD006');
