INSERT INTO `product` VALUES ('159', '顶级洋甘菊口服精油 10ml', 'images/product/20111202183321947.jpg', null, null, '72', '<p style=\\\"text-indent:52.5pt;\\\">\r\n	<span style=\\\"font-family:宋体;\\\">顶级洋甘菊精油（食品级）：</span>\r\n</p>\r\n<p style=\\\"text-indent:52.5pt;\\\">\r\n	<span><span>&nbsp;&nbsp; </span></span><span style=\\\"font-family:宋体;\\\">专家特别推荐：</span>\r\n</p>\r\n<p style=\\\"text-indent:52.5pt;\\\">\r\n	<span><span>&nbsp;&nbsp; </span></span><span style=\\\"font-family:宋体;\\\">宝迪顶级洋甘菊口服精油</span><span>------</span><span style=\\\"font-family:宋体;\\\">清凉、镇静、安眠、防敏的作用著称。</span>\r\n</p>\r\n<p style=\\\"text-indent:52.5pt;\\\">\r\n	<span style=\\\"font-family:宋体;\\\">功效：</span>\r\n</p>\r\n<p style=\\\"margin-left:70.5pt;text-indent:-18.0pt;\\\">\r\n	<span><span>1，<span>&nbsp; </span></span></span><span style=\\\"font-family:宋体;\\\">改善失眠及神经衰弱，</span>\r\n</p>\r\n<p style=\\\"margin-left:70.5pt;text-indent:-18.0pt;\\\">\r\n	<span><span>2，<span>&nbsp; </span></span></span><span style=\\\"font-family:宋体;\\\">改善敏感肌肤，改善晒伤和眼部疲劳的问题</span>\r\n</p>\r\n<p style=\\\"margin-left:70.5pt;text-indent:-18.0pt;\\\">\r\n	<span><span>3，<span>&nbsp; </span></span></span><span style=\\\"font-family:宋体;\\\">缓解消化不良、肠胃气胀、腹部绞痛、食欲不振等消化问题</span>\r\n</p>\r\n<p style=\\\"margin-left:70.5pt;text-indent:-18.0pt;\\\">\r\n	<span><span>4，<span>&nbsp; </span></span></span><span style=\\\"font-family:宋体;\\\">洋甘菊具有滋补子宫、卵巢、改善盆腔发炎等症状有很好的治疗作用</span>\r\n</p>', '早起空腹与乳化剂适量加1D口服洋甘菊精油一起调配，温热水服食。<br />', '顶级纯天然进口有机洋甘菊精油', '2011-12-02 18:32:19', '', 'zh_cn', 'BD807');
