INSERT INTO `news` VALUES ('243', '29', '赤峰学院', '<div>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:&prime;宋体&prime;;color:#000000;font-size:16pt;font-weight:bold;\">赤峰学院成人高考招生专业明细</span><span style=\"font-family:&prime;宋体&prime;;color:#000000;font-size:16pt;font-weight:bold;\"></span>\r\n	</p>\r\n	<p style=\"text-align:left;\">\r\n		<span style=\"font-family:&prime;宋体&prime;;color:#000000;font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:left;\">\r\n		<span style=\"font-family:&prime;宋体&prime;;color:#000000;font-size:15pt;\">学院简介：</span><span style=\"font-family:&prime;宋体&prime;;color:#000000;font-size:15pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:left;text-indent:18pt;\">\r\n		<span style=\"font-family:&prime;宋体&prime;;color:#000000;font-size:12pt;\">赤峰学院是2003年经国家教育部批准，由具有40多年办学历史、在社会上享有良好声誉的赤峰民族师范高等专科学校与赤峰教育学院、内蒙古广播电视大学赤峰分校、赤峰卫生学校、内蒙古幼儿师范学校合并组建的一所多科性本科普通高等学校。2008年赤峰艺术旅游学校并入赤峰学院。学院位于闻名遐迩的“红山文化”发祥地——内蒙古赤峰市。</span><span style=\"font-family:&prime;仿宋_GB2312&prime;;color:#000000;font-size:12pt;\">&nbsp;</span><span style=\"font-family:&prime;仿宋_GB2312&prime;;color:#000000;font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:left;text-indent:24pt;\">\r\n		<span style=\"font-family:&prime;宋体&prime;;color:#000000;font-size:12pt;\">学院教育教学管理体系完善，内设党务、行政、教辅、后勤等机构19个，设有蒙古文史学院、文学院、外国语学院、音乐学院、美术学院、政法学院、历史文化学院、经济与管理学院、教育科学学院、体育学院、数学与统计学院、资源与环境科学学院、物理与电子信息工程学院、计算机与信息工程学院、化学化工学院、生命科学学院、建筑与机械工程学院、初等教育学院、医学院、社会科学部、大学外语教学部、成人教育学院等22个教学院、部。专业设置涵盖文学、史学、法学、教育学、理学、工学、医学、管理学、艺术学等九个学科门类，开设39个本科专业和59个高职高专专业，部分专业采用蒙汉两种语言授课。现有全日制本专科学生8768人，成人教育学生6004人。学院设有附属医院、第二附属医院、附属中学三个附属单位。</span><span style=\"font-family:&prime;宋体&prime;;color:#000000;font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-align:left;text-indent:24pt;\">\r\n		<span style=\"font-family:&prime;宋体&prime;;color:#000000;font-size:12pt;\">学院师资力量雄厚，现有专任教师878人，其中教授109人，副教授312人，硕士研究生导师16人，具有博士、硕士学位教师371人，享受国务院政府特殊津贴教师4人，自治区有突出贡献中青年专家3人，自治区“321”人才工程第一、二层次人选3人，“111”人才工程人选1人，中国工艺美术大师1人,自治区教学名师2人，赤峰市“十百千学术技术带头人”&nbsp;4人。</span><span style=\"font-family:&prime;宋体&prime;;color:#000000;font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:&prime;宋体&prime;;font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:&prime;宋体&prime;;font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:&prime;宋体&prime;;font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:&prime;宋体&prime;;font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:&prime;宋体&prime;;font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:&prime;宋体&prime;;font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:&prime;宋体&prime;;font-size:14pt;font-weight:bold;\">专科<span>/</span><span>本科招生专业明细：</span></span><span style=\"font-family:&prime;宋体&prime;;font-size:14pt;font-weight:bold;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:&prime;宋体&prime;;font-size:10.5pt;\"></span>\r\n	</p>\r\n	<table style=\"border-collapse:collapse;\" align=\"center\">\r\n		<tbody>\r\n			<tr>\r\n				<td style=\"border-bottom:#000000 0.5pt solid;border-left:#000000 0.5pt solid;border-top:#000000 0.5pt solid;border-right:#000000 0.5pt solid;\" valign=\"top\" width=\"145\">\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"font-family:&prime;宋体&prime;;font-size:10.5pt;\">招生专业</span><span style=\"font-family:&prime;宋体&prime;;font-size:10.5pt;\"></span>\r\n					</p>\r\n				</td>\r\n				<td style=\"border-bottom:#000000 0.5pt solid;border-left:#000000 0.5pt solid;border-top:#000000 0.5pt solid;border-right:#000000 0.5pt solid;\" valign=\"center\" width=\"102\">\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"font-family:&prime;宋体&prime;;font-size:10.5pt;\">学习形式</span><span style=\"font-family:&prime;宋体&prime;;font-size:10.5pt;\"></span>\r\n					</p>\r\n				</td>\r\n				<td style=\"border-bottom:#000000 0.5pt solid;border-left:#000000 0.5pt solid