INSERT INTO `news` VALUES ('6324', '30', '吉林建筑工程学院', '<div>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:′宋体′;font-size:16pt;font-weight:bold;\">吉林建筑工程学院成人高考招生明细</span><span style=\"font-family:′宋体′;font-size:16pt;font-weight:bold;\"></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:′宋体′;font-size:16pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:′宋体′;font-size:16pt;\">学院简介：</span><span style=\"font-family:′宋体′;font-size:16pt;\"></span> \r\n	</p>\r\n	<p style=\"text-indent:24pt;\">\r\n		<span style=\"font-family:′Calibri′;font-size:12pt;\">吉林建筑工程学院是吉林省重点建设的普通本科高等学校，坐落在吉林省省会长春市净月经济开发区。学校始建于<span>1956</span><span>年，原为国家城市建设部长春城市建设工程学校，是新中国首批成立的十所建筑类专门学校之一。</span><span>1960</span><span>年升格为本科高等学校，更名为</span><span>“</span><span>吉林建筑工程学院</span><span>”</span><span>，</span><span>1962</span><span>年停办并改回中专，</span><span>1978</span><span>年</span><span>12</span><span>月，经国务院批准恢复本科建制，</span><span>1997</span><span>年通过了原国家教委本科教学工作合格评价，</span><span>2003</span><span>年取得硕士学位授予权，</span><span>2008</span><span>年以优秀成绩通过国家教育部本科教学水平评估。</span></span><span style=\"font-family:′宋体′;font-size:12pt;\"></span> \r\n	</p>\r\n	<p style=\"text-indent:18pt;\">\r\n		<span style=\"font-family:′Calibri′;font-size:12pt;\">学校拥有土木工程、建筑学、环境科学与工程等<span>3</span><span>个一级学科为吉林省优势特色重点学科，具有建筑学、土木工程、环境科学与工程、材料科学与工程、管理科学与工程、城乡规划学、设计学等</span><span>7</span><span>个硕士学位授权一级学科点以及企业管理、马克思主义中国化、思想政治教育等</span><span>3</span><span>个硕士学位授权二级学科点，还拥有建筑与土木工程、工业设计工程等</span><span>2</span><span>个工程硕士学位授权点。</span></span><span style=\"font-family:′宋体′;font-size:12pt;\"></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:′宋体′;font-size:10.5pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:′宋体′;font-size:14pt;\">招生专业：</span><span style=\"font-family:′宋体′;font-size:14pt;\"></span> \r\n	</p>\r\n	<table style=\"border-collapse:collapse;\">\r\n		<tbody>\r\n			<tr>\r\n				<td style=\"border-bottom:#000000 0.5pt solid;border-left:#000000 0.5pt solid;border-top:#000000 0.5pt solid;bo