INSERT INTO `news` VALUES ('4432', '31', '吉林农业大学', '<div>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:&prime;宋体&prime;;font-size:16pt;font-weight:bold;\">吉林农业大学成人高考专业招生明细</span><span style=\"font-family:&prime;Calibri&prime;;font-size:16pt;font-weight:bold;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:&prime;Calibri&prime;;font-size:14pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:&prime;宋体&prime;;font-size:14pt;\">学院简介</span><span style=\"font-family:&prime;Calibri&prime;;font-size:14pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:&prime;宋体&prime;;font-size:12pt;\">&nbsp;&nbsp;&nbsp;吉林农业大学创建于<span>1948</span><span>年，是吉林省属重点大学，是我国首批具有学士、硕士学位授予权的高等院校之一，</span><span>1993</span><span>年成为吉林省省属院校中最早获得博士学位授权单位的高校。学校办学条件优越，师资力量雄厚，校园风光别致，是治学育人的佳园。学校现有教职工</span><span>1800</span><span>余人，其中，专任教师</span><span>1051</span><span>人，高级专业技术职称</span><span>499</span><span>人，中国工程院院士</span><span>1</span><span>人，国务院学科评议组成员</span><span>1</span><span>人。形成了结构合理、素质优良、充满活力的高水平师资队伍。学校现有各类全日制在校生</span><span>18000</span><span>余人，成人本、专科函授生在校生规模稳定在</span><span>9000</span><span>人以上</span></span><span style=\"font-family:&prime;宋体&prime;;font-size:16pt;font-weight:bold;\">。</span><span style=\"font-family:&prime;Calibri&prime;;font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:&prime;宋体&prime;;font-size:14pt;\">专业明细</span><span style=\"font-family:&prime;宋体&prime;;font-size:16pt;\">：</span><span style=\"font-family:&prime;Calibri&prime;;font-size:16pt;\"></span>\r\n	</p>\r\n	<table style=\"border-collapse:collapse;\">\r\n		<tbody>\r\n			<tr>\r\n				<td style=\"border-bottom:#000000 0.5pt solid;border-left:#000000 0.5pt solid;border-top:#000000 0.5pt solid;border-right:#000000 0.5pt solid;\" valign=\"top\" rowspan=\"18\" width=\"183\">\r\n					<p>\r\n						<span style=\"font-family:&prime;Calibri&prime;;font-size:16pt;font-weight:bold;\"></span>\r\n					</p>\r\n					<p>\r\n						<span style=\"font-family:&prime;Ca