INSERT INTO `news` VALUES ('234', '32', '吉林医药学院', '<div>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:&prime;宋体&prime;;font-size:16pt;\">吉林医药学院成人高考专业明细</span><span style=\"font-family:&prime;Calibri&prime;;font-size:16pt;\"></span>\r\n	</p>\r\n	<p style=\"background:#ffffff;\">\r\n		<span style=\"font-family:&prime;宋体&prime;;font-size:14pt;\">学院介绍</span><span style=\"font-family:&prime;宋体&prime;;font-size:14pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:15.75pt;background:#ffffff;\">\r\n		<span style=\"font-family:&prime;宋体&prime;;font-size:12pt;\">吉林医药学院前身为中国人民解放军第四军医大学吉林军医学院，创建于１９５２年，共为军队和地方培养了３万多名医务人才，具有光辉的历史和优良的传统。２００４年８月，经国务院、中央军委批准，学校由军队整体移交吉林省，更名为吉林医药学院，成为吉林省省属公办高等医学本科院校，是目前吉林省惟一独立设置的综合性高等医科院校。</span><span style=\"font-family:&prime;宋体&prime;;font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:&prime;宋体&prime;;font-size:14pt;\">专业明细：</span><span style=\"font-family:&prime;Calibri&prime;;font-size:10.5pt;\"></span>\r\n	</p>\r\n	<table style=\"border-collapse:collapse;\">\r\n		<tbody>\r\n			<tr>\r\n				<td style=\"border-bottom:#000000 0.5pt solid;border-left:#000000 0.5pt solid;border-top:#000000 0.5pt solid;border-right:#000000 0.5pt solid;\" valign=\"top\" rowspan=\"6\" width=\"189\">\r\n					<p>\r\n						<span style=\"font-family:&prime;Calibri&prime;;font-size:10.5pt;\"></span>\r\n					</p>\r\n					<p>\r\n						<span style=\"font-family:&prime;宋体&prime;;font-size:16pt;\">高中起点专科</span><span style=\"font-family:&prime;Calibri&prime;;font-size:16pt;\"></span>\r\n					</p>\r\n					<p>\r\n						<span style=\"font-family:&prime;宋体&prime;;font-size:16pt;\">专业起点本科</span><span style=\"font-family:&prime;Calibri&prime;;font-size:16pt;\"></span>\r\n					</p>\r\n					<p>\r\n						<span style=\"font-family:&prime;宋体&prime;;font-size:16pt;\">2.5<span>年毕业</span></span><span style=\"font-family:&prime;Calibri&prime;;font-size:10.5pt;\"></span>\r\n					</p>\r\n				</td>\r\n				<td style=\"border-bottom:#000000 0.5pt solid;bor