INSERT INTO `news` VALUES ('24', '35', '北京大学', '<div>\r\n	<p style=\"text-align:center;background:#ffffff;\">\r\n		<span style=\"font-family:&prime;宋体&prime;;font-size:16pt;\">北京大学网络教育学院招生</span><span style=\"font-family:&prime;宋体&prime;;font-size:16pt;\">专业明细</span><span style=\"font-family:&prime;宋体&prime;;font-size:16pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:&prime;Calibri&prime;;font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24pt;\">\r\n		<span style=\"font-family:&prime;宋体&prime;;font-size:12pt;\">北京大学创办于</span><span style=\"font-family:&prime;Times New Roman&prime;;font-size:12pt;\">1898</span><span style=\"font-family:&prime;宋体&prime;;font-size:12pt;\">年，是第一所国立综合性大学，以优良的治学校风，齐全的学科门类，雄厚的师资力量，显著的科研成果而著称。为迎接信息时代的挑战和落实科教兴国战略，北京大学利用现代信息技术，积极开展网络教育，于</span><span style=\"font-family:&prime;Times New Roman&prime;;font-size:12pt;\">1998</span><span style=\"font-family:&prime;宋体&prime;;font-size:12pt;\">年初开始筹建现代远程教育，面向成人学习者提供优质远程高等教育，并于</span><span style=\"font-family:&prime;Times New Roman&prime;;font-size:12pt;\">1999</span><span style=\"font-family:&prime;宋体&prime;;font-size:12pt;\">年开始招生。</span><span style=\"font-family:&prime;Times New Roman&prime;;font-size:12pt;\">2005</span><span style=\"font-family:&prime;宋体&prime;;font-size:12pt;\">年</span><span style=\"font-family:&prime;Times New Roman&prime;;font-size:12pt;\">&nbsp;3</span><span style=\"font-family:&prime;宋体&prime;;font-size:12pt;\">月，北京大学网络教育学院正式成立，更好地整合了北京大学的优质教育资源，开展北京大学现代远程教育的教学、管理、技术开发等工作。</span><span style=\"font-family:&prime;Calibri&prime;;font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:&prime;Calibri&prime;;font-size:12pt;\"></span>\r\n	</p>\r\n	<table style=\"border-collapse:collapse;\">\r\n		<tbody>\r\n			<tr>\r\n				<td style=\"border-bottom:#000000 0.5pt solid;border-left:#000000 0.5pt solid;border-top:#000000 0.5pt solid;border-right:#000000 0.5pt solid;\" valign=\"top\" width=\"142\">\r\n					<p>\r\n						<span style=\"font-family: