INSERT INTO `news` VALUES ('4324', '36', '北京语言大学', '<div>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:&prime;宋体&prime;;color:#333333;font-size:14pt;\">北京语言大学网络教育学院招生专业明细</span><span style=\"font-family:&prime;宋体&prime;;color:#333333;font-size:14pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:&prime;宋体&prime;;color:#333333;font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24pt;\">\r\n		<span style=\"font-family:&prime;宋体&prime;;color:#333333;font-size:12pt;\">经国家教育部批准，北京语言大学网络教育学院（简称“北语网院”）于2000年成立。经教育部批准，北语网院可自主确定本、专科教育入学标准和考试、录取方式，对达到要求的学生颁发国家承认的文凭和授予学位。从2001年开始，北语网院充分利用校内外中外优秀的师资、丰富的学习资源，逐步开展网上对外汉语、英语、金融学、日语等专业的学历教育。在新的形势下，北语网院强调以学生为中心，注重规范管理、强调服务、注重质量、培养能力。加强体制管理以更好地发挥网络教育开放性、学生自主学习的特点，通过多种方式提供多样化的助学服务使学生能顺利完成学业并得到社会的认可。</span><span style=\"font-family:&prime;宋体&prime;;color:#333333;font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:&prime;宋体&prime;;color:#333333;font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:&prime;宋体&prime;;color:#333333;font-size:12pt;\">招生专业明细表：</span><span style=\"font-family:&prime;宋体&prime;;color:#333333;font-size:12pt;\"></span>\r\n	</p>\r\n	<table style=\"border-collapse:collapse;\">\r\n		<tbody>\r\n			<tr>\r\n				<td style=\"border-bottom:#000000 0.5pt solid;border-left:#000000 0.5pt solid;border-top:#000000 0.5pt solid;border-right:#000000 0.5pt solid;\" valign=\"top\" width=\"101\">\r\n					<p>\r\n						<span style=\"font-family:&prime;宋体&prime;;font-size:12pt;font-weight:bold;\">招生层次</span><span style=\"font-family:&prime;Calibri&prime;;font-size:12pt;font-weight:bold;\"></span>\r\n					</p>\r\n				</td>\r\n				<td style=\"border-bottom:#000000 0.5pt solid;border-left:#000000 0.5pt solid;border-top:#000000 0.5pt solid;border-right:#000000 0.5pt solid;\" valign=\"top\" width=\"273\">\r\n					<p>\r\n						<span style=\"font-family:&prime;宋体&prime;;font-size:12pt;font-weight:bold;\">专业名称</span><span style=\"font-family:&prime;Calibri&prim