INSERT INTO `news` VALUES ('423', '37', '北京理工大学', '<div>\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"font-family:&prime;宋体&prime;;font-size:16pt;\">北京理工大学现代远程教育学院招生专业明细</span><span style=\"font-family:&prime;宋体&prime;;font-size:16pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:&prime;宋体&prime;;font-size:12pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:12pt;\">\r\n		<span style=\"font-family:&prime;宋体&prime;;font-size:12pt;\">北京理工大学现代远程教育学院成立于<span>2000</span><span>年，是经教育部</span><span>2000</span><span>年批准的</span><span>15</span><span>所现代远程教育试点高校之一。学校十分重视发展现代远程教育，将其列入了学校中长期发展规划。学院坚持“依法办学、规范管理、注重质量、提高效益”的工作方针，以培养优秀应用型人才为目标，奉行一切为了学生学习与发展的办学理念，注重培养学生网络环境下的自主学习能力与实践能力，致力于不断探索总结现代远程教育规律，努力构建学校、校外学习中心、教师及学生协同发展体，不断完善学习支持服务体系，实现现代远程教育“规模、质量、结构、效益”协调可持续发展。现有在校生</span><span>18250</span><span>人，已有毕业生</span><span>7816</span><span>人，其中</span><span>1821</span><span>人获得了学士学位。</span></span><span style=\"font-family:&prime;宋体&prime;;font-size:12pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:&prime;宋体&prime;;font-size:14pt;\"></span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-family:&prime;宋体&prime;;font-size:14pt;\">招生专业：</span><span style=\"font-family:&prime;宋体&prime;;font-size:14pt;\"></span>\r\n	</p>\r\n	<table style=\"border-collapse:collapse;\" align=\"center\">\r\n		<tbody>\r\n			<tr>\r\n				<td style=\"border-bottom:#000000 0.5pt solid;border-left:#000000 0.5pt solid;border-top:#000000 0.5pt solid;border-right:#000000 0.5pt solid;\" valign=\"top\" rowspan=\"2\" width=\"283\">\r\n					<p style=\"text-align:center;\">\r\n						<span style=\"font-family:&prime;宋体&prime;;color:#000000;font-size:14pt;font-weight:bold;\">招生专业</span><span style=\"font-family:&prime;宋体&prime;;color:#000000;font-size:14pt;font-weight:bold;\"></span>\r\n					</p>\r\n				</td>\r\n				<td style=\"border-bottom:#000000 0.5pt solid;border-left:#000000 0.5pt solid;border-top:#000000 0.5pt solid;border-right:#000000 0.5pt solid;\" valign=\"top\" width=\"284\" colspan=